CREATE TABLE EMPREGADO (
  COD_EMP NUMBER(6,0),
  NOME VARCHAR2(20 BYTE),
  SOBRENOME VARCHAR2(25 BYTE) CONSTRAINT EMP_LAST_NAME_NN NOT NULL ENABLE,
