  TELEFONE VARCHAR2(20 BYTE),
  DATA_CONTRATACAO DATE CONSTRAINT EMP_HIRE_DATE_NN NOT NULL ENABLE,
  COD_SERVICO VARCHAR2(10 BYTE) CONSTRAINT EMP_JOB_NN NOT NULL ENABLE,
  SALARIO NUMBER(8,2),
  PCT_COMISSAO NUMBER(2,2),
  COD_GERENTE NUMBER(6,0),
  COD_DEP NUMBER(4,0),
